jobs = {}

def parse_dob(dob_str: str):
    if not dob_str:
        return None
    dob_str = dob_str.strip()
    for fmt in ("%d-%m-%Y", "%d/%m/%Y"):
        try:
            return datetime.strptime(dob_str, fmt).date()
        except Exception:
            continue
    import re
    parts = re.findall(r"(\d{1,2})", dob_str)
    if len(parts) >= 2:
        a, b = int(parts[0]), int(parts[1])
        day, month = (a, b) if a > 12 else (b, a) if b > 12 else (a, b)
        try:
            return datetime(year=2000, month=month, day=day).date()
        except Exception:
            return None
    return None


def today_local():
    if ZoneInfo is not None:
        return datetime.now(tz=ZoneInfo("Asia/Kolkata")).date()
    return datetime.now().date()


def safe_filename_roll(rollnumber: str) -> str:
    return "".join(c for c in rollnumber if c.isalnum() or c in ("_", "-")).strip() or "user"


