def run_email_job(job_id, csv_text, sender, app_password, smtp_server, smtp_port, template_html, preview_date=None):
    job = jobs[job_id]
    job["status"] = "running"
    job["logs"] = []
    job["sent"] = 0
    job["skipped"] = 0
    job["errors"] = 0

    def log(msg):
        job["logs"].append(msg)

    output_dir = Path(tempfile.gettempdir()) / f"bday_job_{job_id}"
    output_dir.mkdir(exist_ok=True)

    try:
        today = preview_date if preview_date else today_local()
        log(f"[INFO] Date used: {today.isoformat()}")

        with smtplib.SMTP(smtp_server, smtp_port) as smtp:
            smtp.starttls()
            smtp.login(sender, app_password)
            log(f"[INFO] SMTP login successful")

            reader = csv.DictReader(io.StringIO(csv_text))
            for rownum, row in enumerate(reader, start=1):
                name = (row.get("name") or "").strip()
                email = (row.get("email") or "").strip()
                dob_raw = (row.get("dob") or row.get("date_of_birth") or "").strip()
                rollnumber = (row.get("rollnumber") or str(rownum)).strip()

                if not name or not email:
                    log(f"[SKIP] Row {rownum}  missing name/email")
                    job["skipped"] += 1
                    continue

                dob_parsed = parse_dob(dob_raw)
                if not dob_parsed:
                    log(f"[SKIP] Row {rownum}  bad DOB '{dob_raw}' for {name}")
                    job["skipped"] += 1
                    continue

                if (dob_parsed.day, dob_parsed.month) != (today.day, today.month):
                    continue

                log(f"[MATCH] {name} <{email}>  DOB {dob_parsed.strftime('%d-%m')}")

                fname = safe_filename_roll(rollnumber)
                html_path = output_dir / f"{fname}.html"
                image_path = output_dir / f"{fname}.png"

                personalized = template_html.replace("{{name}}", name)
                html_path.write_text(personalized, encoding="utf-8")

                try:
                    subprocess.run([
                        "wkhtmltoimage",
                        "--enable-local-file-access",
                        "--disable-smart-width",
                        "--width", "1200",
                        "--quality", "100",
                        str(html_path), str(image_path)
                    ], check=True, capture_output=True)
                    use_image = True
                except Exception as e:
                    log(f"[WARN] wkhtmltoimage failed ({e}), sending HTML fallback")
                    use_image = False

                cid = make_msgid()
                msg = EmailMessage()
                msg["From"] = sender
                msg["To"] = email
                msg["Subject"] = f"Happy Birthday {name} "
                msg.set_content(f"Happy Birthday {name}!\nWishing you a wonderful day.")

                if use_image:
                    msg.add_alternative(f"""
                    <html><body style="margin:0;text-align:center;background:#f2f2f2;">
                    <img src="cid:{cid[1:-1]}" style="max-width:100%;height:auto;">
                    </body></html>""", subtype="html")
                    with open(image_path, "rb") as img:
                        img_data = img.read()
                    html_part = msg.get_payload()[1]
                    html_part.add_related(img_data, maintype="image", subtype="png",
                                          cid=cid, filename="Birthday Greetings.png", disposition="inline")
                else:
                    msg.add_alternative(personalized, subtype="html")

                try:
                    smtp.send_message(msg)
                    log(f"[SENT] {name} ({rollnumber})  {email}")
                    job["sent"] += 1
                except Exception as e:
                    log(f"[ERROR] Failed to send to {email}: {e}")
                    job["errors"] += 1

        job["status"] = "done"
        log(f"[DONE] {job['sent']} emails sent, {job['skipped']} skipped, {job['errors']} errors")

    except Exception as e:
        job["status"] = "error"
        log(f"[FATAL] {e}")
    finally:
        import shutil
        shutil.rmtree(output_dir, ignore_errors=True)


@app.route("/")
def index():
    return render_template("index.html")


@app.route("/api/preview-csv", methods=["POST"])
def preview_csv():
    """Return first 10 rows + column names from uploaded CSV"""
    file = request.files.get("csv")
    if not file:
        return jsonify({"error": "No file"}), 400
    text = file.read().decode("utf-8", errors="replace")
    reader = csv.DictReader(io.StringIO(text))
    rows = []
    for i, row in enumerate(reader):
        if i >= 10:
            break
        rows.append(dict(row))
    return jsonify({"columns": reader.fieldnames or [], "rows": rows, "csv_text": text})


@app.route("/api/send", methods=["POST"])
def send_emails():
    data = request.json
    required = ["csv_text", "sender", "app_password"]
    for r in required:
        if not data.get(r):
            return jsonify({"error": f"Missing field: {r}"}), 400

    job_id = str(uuid.uuid4())[:8]
    jobs[job_id] = {"status": "queued", "logs": [], "sent": 0, "skipped": 0, "errors": 0}

    preview_date = None
    if data.get("preview_date"):
        try:
            preview_date = datetime.strptime(data["preview_date"], "%Y-%m-%d").date()
        except Exception:
            pass

    template = data.get("template.html") or TEMPLATE_HTML_DEFAULT

    t = threading.Thread(
        target=run_email_job,
        args=(job_id, data["csv_text"], data["sender"], data["app_password"],
              data.get("smtp_server", "smtp.gmail.com"),
              int(data.get("smtp_port", 587)),
              template, preview_date),
        daemon=True
    )
    t.start()

    return jsonify({"job_id": job_id})


@app.route("/api/job/<job_id>")
def job_status(job_id):
    job = jobs.get(job_id)
    if not job:
        return jsonify({"error": "Not found"}), 404
    return jsonify(job)


